using Microsoft.AspNetCore.Http;
using EVO_WebApi.Resources;

namespace EVO_WebApi.Controllers
{ 
    /// <summary>
    /// 
    /// </summary>
    [ApiController]
    public class DocumentosApiController : ControllerBase
    {
        #region Campos Privados
        private static readonly Logger logger = LogManager.GetCurrentClassLogger();
        private IMapper mapper;
        #endregion

        #region Métodos
        /// <summary>
        /// Obtiene los documentos
        /// </summary>
        /// <response code="200">Operación realizada con éxito</response>
        [HttpGet]
        [Route("/api/documentos")]
        [ValidateModelState]
        [SwaggerOperation("ObtenerDocumentos")]
        [SwaggerResponse(statusCode: 200, type: typeof(List<DocumentoResponse>), description: "Operación realizada con éxito")]
        public virtual IActionResult ObtenerDocumentos()
        {
            logger.Info($"Entró al método ObtenerDocumentos en DocumentosApi");

            try
            {
                BLDocumento bLDocumento = new BLDocumento();

                List<BODocumento> bODocumentos = bLDocumento.ObtenerDocumentos();

                List<DocumentoResponse> documentosResponse  = this.mapper.Map<List<BODocumento>, List<DocumentoResponse>>(bODocumentos);

                return Ok(documentosResponse);
            }
            catch (EVOException e)
            {
                return StatusCode(StatusCodes.Status400BadRequest, e.Message);
            }
            catch
            {
                return StatusCode(StatusCodes.Status500InternalServerError, errores.errGeneral);
            }
        }
        #endregion

        #region Contructores
        /// <summary>
        /// 
        /// </summary>
        /// <param name="mapper"></param>
        public DocumentosApiController(IMapper mapper)
        {
            this.mapper = mapper;
        }
        #endregion
    }
